# Main setup function
main() {
    log_info "ðŸš€ Starting Silvertine Trading System Setup"
    log_info "This will install all dependencies and configure the development environment"
    echo
    
    # Parse command line arguments
    local skip_system=false
    local skip_validation=false
    
    while [[ $# -gt 0 ]]; do
        case $1 in
            --skip-system)
                skip_system=true
                shift
                ;;
            --skip-validation)
                skip_validation=true
                shift
                ;;
            --help|-h)
                echo "Usage: $0 [options]"
                echo "Options:"
                echo "  --skip-system      Skip system dependency installation"
                echo "  --skip-validation  Skip validation tests"
                echo "  --help, -h         Show this help message"
                exit 0
                ;;
            *)
                log_error "Unknown option: $1"
                exit 1
                ;;
        esac
    done
    
    # Setup steps
    if [[ "$skip_system" != true ]]; then
        install_system_deps
    fi
    
    check_python
    install_poetry
    install_talib_c
    install_python_deps
    setup_redis
    setup_config
    setup_ide
    setup_runtime_dirs
    
    if [[ "$skip_validation" != true ]]; then
        validate_installation
    fi
    
    show_next_steps
    
    log_success "Setup completed! ðŸŽ¯"
}

# Run main function
main "$@"